mapper.select:
select Customer_ID, Abbrev, Customer_Na from customer where Customer_ID in
#{customerId,jdbcType=VARCHAR}
and delete_flag = 1

select   Customer_ID, Abbrev, Customer_Na, PractiseDate, ShutOutDate, FictitiousPerson, TaxNumber,
          Keyman, Mobile, Email, Fax, Tel1,Mathod,ShutOut
from customer


        and Customer_Na like concat('%',#{customerNa},'%')


        and FictitiousPerson = #{fictitiousperson}


        and Keyman = #{keyman}


        and Mobile = #{mobile}


        and Mathod = #{mathod}

       and delete_flag = 1

mapper.update:
update customer
set delete_flag = #{deleteFlag}
where Customer_ID = #{customerId,jdbcType=VARCHAR} and delete_flag != #{deleteFlag}
